package blockutils

import "testing"

var digibytetxcoinbase = ""
var digibytetx = ""

func TestDigiByteTx(t *testing.T) {
	tx, err := NewTransactionFromHexString(digibytetx)
	if err != nil {
		t.Fatalf("Could not parse tx hex; %s", err)
	}

	if tx.Version != 1 {
		t.Fatalf("Invalid tx version. Expect %d, got %d", 1, tx.Version)
	}

	if tx.Hashstr != "d0e075c1e5c52854a5b5386e89bd6436c767a2570901d38537703baef3a313ef" {
		t.Fatal("TX ID did not match for digibyte tx")
	}

	if len(tx.Vin) != 2 {
		t.Fatalf("Invalid input count. Expected %d, found %d", 2, len(tx.Vin))
	}
}
